import React, { useEffect, useState, useRef } from "react";
import "../css/landing.css";
import { motion } from "framer-motion";
import { Link, useNavigate, useLocation } from "react-router-dom";
import "../css/login.css";

function Form(props) {
  const [clientName, setClientName] = useState("");
  const [dateOfBirth, setDateOfBirth] = useState("");
  const [submitMsg, setSubmitMsg] = useState("");
  const [clinic, setClinic] = useState("");
  const [dateNeededBy, setDateNeededBy] = useState("");

  const navigate = useNavigate();
  const location = useLocation();

  const { formChoice } = props;

  function handleLocationChoice(e) {
    setClinic(e.target.value);
  }

  function updateForm(e) {
    if (e.target.id === "clientName") {
      setClientName(e.target.value);
    }
    if (e.target.id === "dateOfBirth") {
      setDateOfBirth(e.target.value);
    }
    if (e.target.id === "dateNeededBy") {
      setDateNeededBy(e.target.value);
    }
  }

  if (props.formChoice === "formulaAndFood") {
    const handleSubmit = async (e) => {
      e.preventDefault();

      if (clientName == "" || dateOfBirth == "") {
        setSubmitMsg("unfilled");
        return;
      } else setSubmitMsg("");

      async function sendForm() {
        const response = await fetch(
          "http://127.0.0.1:4000/pdf/formula-and-food",
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              formChoice,
              clientName,
              dateOfBirth,
              clinic,
            }),
          }
        );
        const user = await response.json();
      }
      sendForm().then(() => {
        navigate(
          "../submitted",
          { state: { formChoice, clientName, dateOfBirth } },
          { replace: true }
        );
      });
    };

    return (
      <div className="formField">
        <form action="POST" onSubmit={handleSubmit}>
          <select
            defaultValue="default"
            onChange={(e) => handleLocationChoice(e)}
            className="defaultSelect"
          >
            <option value="default">Choose your usual office location</option>
            <option value="familyCareNorth">HCN FAMILY CARE NORTH</option>
            <option value="childrensCareNorth">
              HCN CHILDREN’S CARE NORTH
            </option>
            <option value="totalWomensCare">HCN TOTAL WOMEN’S CARE</option>
            <option value="nicholsPediatricAtGreaterNaplesYMCA">
              HCN NICHOLS PEDIATRIC AT GREATER NAPLES YMCA
            </option>
            <option value="childrensCareEast">HCN CHILDREN’S CARE EAST</option>
            <option value="dentalCareEast">HCN DENTAL CARE EAST</option>

            <option value="friendshipHealth">HCN FRIENDSHIP HEALTH</option>
            <option value="marionEFether">HCN AT MARION E. FETHER</option>
            <option value="floridaStateUniversityCollegeOfMedicine">
              HCN AT FLORIDA STATE UNIVERSITY, COLLEGE OF MEDICINE
            </option>
            <option value="familyCareAtItech">HCN FAMILY CARE AT ITECH</option>
            <option value="nicholsCommunityHealthCenter">
              HCN AT NICHOLS COMMUNITY HEALTH CENTER
            </option>
          </select>
          <input
            type="text"
            id="dateNeededBy"
            value={dateNeededBy}
            onChange={(e) => updateForm(e)}
            placeholder="Date Form Needed By"
            className="defaultBtn"
          />
          <input
            type="text"
            id="clientName"
            value={clientName}
            onChange={(e) => updateForm(e)}
            placeholder="Client's name"
            className="defaultBtn"
          />
          <input
            type="text"
            id="dateOfBirth"
            value={dateOfBirth}
            onChange={(e) => updateForm(e)}
            placeholder="Date of Birth"
            className="defaultBtn"
          />
          <input type="submit" className="defaultSubmitBtn" value="Submit" />
        </form>
        {submitMsg == "unfilled" && (
          <p className="failedSubmit">
            Not every form input was filled. Try again please.
          </p>
        )}
        <div className="loginToSignup">
          <p className="dontLogin">By clicking "Submit", you agree to our</p>
          <Link to="../terms-of-use" className="loginLinkToSignup">
            terms
          </Link>
        </div>
        <p className="confirmAge">and confirm you're 18 years or older.</p>
      </div>
    );
  } else {
    return <div></div>;
  }
}

export default Form;
