import React, { useEffect, useState, useRef } from "react";
import "../css/landing.css";
import { motion } from "framer-motion";
import { Link, useNavigate, useLocation } from "react-router-dom";
import "../css/login.css";
const { PDFDocument, StandardFonts, rgb } = require("pdf-lib");
const { writeFileSync, readFileSync } = require("fs");

function Form(formChoice) {
  const [clientName, setClientName] = useState("");
  const [dateOfBirth, setDateOfBirth] = useState("");
  const [submitMsg, setSubmitMsg] = useState("");

  const navigate = useNavigate();
  const location = useLocation();

  function updateForm(e) {
    if (e.target.id === "clientName") {
      setClientName(e.target.value);
    }
    if (e.target.id === "dateOfBirth") {
      setDateOfBirth(e.target.value);
    }
  }

  if (formChoice.formChoice === "formulaAndFood") {
    const handleSubmit = async (e) => {
      e.preventDefault();

      if (clientName == "" || dateOfBirth == "") {
        setSubmitMsg("unfilled");
        return;
      } else setSubmitMsg("");

      const document = await PDFDocument.load(
        readFileSync("../pdfs/medical-documentation-formula-food.pdf")
      );

      const courierBoldFont = await document.embedFont(StandardFonts.Courier);
      const firstPage = document.getPage(0);

      firstPage.moveTo(72, 570);
      firstPage.drawText(new Date().toUTCString(), {
        font: courierBoldFont,
        size: 12,
      });

      firstPage.moveTo(105, 530);
      firstPage.drawText("Ms. Jane,", {
        font: courierBoldFont,
        size: 12,
      });

      firstPage.moveTo(72, 330);
      firstPage.drawText(
        "John Doe \nSr. Vice President Engineering \nLogRocket",
        {
          font: courierBoldFont,
          size: 12,
          lineHeight: 10,
        }
      );

      writeFileSync("jane-doe.pdf", await document.save());
      createPDF().catch((err) => console.log(err));
    };

    return (
      <div className="formField">
        <form action="POST" onSubmit={handleSubmit}>
          <input
            type="text"
            id="clientName"
            value={clientName}
            onChange={(e) => updateForm(e)}
            placeholder="Client's name"
            className="defaultBtn"
          />
          <input
            type="text"
            id="dateOfBirth"
            value={dateOfBirth}
            onChange={(e) => updateForm(e)}
            placeholder="Date of Birth"
            className="defaultBtn"
          />
          <input type="submit" className="defaultSubmitBtn" value="Submit" />
        </form>
        {submitMsg == "unfilled" && (
          <p className="failedSubmit">
            Not every form input was filled. Try again please.
          </p>
        )}
        <div className="loginToSignup">
          <p className="dontLogin">By clicking "Submit", you agree to our</p>
          <Link to="../terms-of-use" className="loginLinkToSignup">
            terms
          </Link>
        </div>
        <p className="confirmAge">and confirm you're 18 years or older.</p>
      </div>
    );
  } else {
    return <div></div>;
  }
}

export default Form;
