import React, { useEffect, useState, useRef } from "react";
import "../css/landing.css";
import { motion } from "framer-motion";
import { Link, useNavigate, useLocation } from "react-router-dom";
import "../css/login.css";
import WebViewer from "@pdftron/webviewer";
import { Document, Page } from "react-pdf";
import samplePDF from "../uploads/medical-documentation-formula-food.pdf";

function Form(formChoice) {
  const [clientName, setClientName] = useState("");
  const [dateOfBirth, setDateOfBirth] = useState("");
  const [submitMsg, setSubmitMsg] = useState("");

  const navigate = useNavigate();
  const location = useLocation();

  const viewer = useRef(null);

  function updateForm(e) {
    if (e.target.id === "clientName") {
      setClientName(e.target.value);
    }
    if (e.target.id === "dateOfBirth") {
      setDateOfBirth(e.target.value);
    }
  }
  /*
  useEffect(() => {
    WebViewer(
      {
        path: "/webviewer/lib",
        initialDoc: "/uploads/medical-documentation-formula-food.pdf",
      },
      viewer.current
    ).then((instance) => {
      const { docViewer, Annotations } = instance;
      const annotatManager = docViewer.getAnnotationManager();

      docViewer.on("documentLoaded", () => {
        const rectangleAnnotat = new Annotations.RectangleAnnotation();
        rectangleAnnotat.PageNumber = 1;
        rectangleAnnotat.X = 100;
        rectangleAnnotat.Y = 150;
        rectangleAnnotat.Width = 200;
        rectangleAnnotat.Height = 50;
        rectangleAnnotat.Author = annotatManager.getCurrentUser();

        annotatManager.addAnnotation(rectangleAnnotat);
        annotatManager.redrawAnnotation(rectangleAnnotat);
      });
    });
  }, []);
*/
  if (formChoice.formChoice === "formulaAndFood") {
    const handleSubmit = async (e) => {
      e.preventDefault();

      if (clientName == "" || dateOfBirth == "") {
        setSubmitMsg("unfilled");
        return;
      } else setSubmitMsg("");
    };

    return (
      <div className="formField">
        <form action="POST" onSubmit={handleSubmit}>
          <input
            type="text"
            id="clientName"
            value={clientName}
            onChange={(e) => updateForm(e)}
            placeholder="Client's name"
            className="defaultBtn"
          />
          <input
            type="text"
            id="dateOfBirth"
            value={dateOfBirth}
            onChange={(e) => updateForm(e)}
            placeholder="Date of Birth"
            className="defaultBtn"
          />
          <input type="submit" className="defaultSubmitBtn" value="Submit" />
        </form>
        {submitMsg == "unfilled" && (
          <p className="failedSubmit">
            Not every form input was filled. Try again please.
          </p>
        )}
        <div className="loginToSignup">
          <p className="dontLogin">By clicking "Submit", you agree to our</p>
          <Link to="../terms-of-use" className="loginLinkToSignup">
            terms
          </Link>
        </div>
        <p className="confirmAge">and confirm you're 18 years or older.</p>
        <Document file={samplePDF}>
          <Page pageNumber={1} />
        </Document>
      </div>
    );
  } else {
    return <div></div>;
  }
}

export default Form;
